                  };
                  SendEmail(email_data_4);

                  setHid({ den: false });
                  setModal1(true);
                  setLoading(false);
                  return;
                } catch (err) {
                  setLoading(false);
                  console.log("Error in BB to BB Transfer function", err);
                }
              }
            }
          }
